mapper.select:
select m.* from (
	select t.item_name as "itemName",t.item_code as "itemCode",ROWNUM rn,t.item_id as "itemId",t.upload_status as "uploadStatus",t.status as "status"
	from biz_tiol_item t 
	where 1 = 1 and t.company_id = #{CURRENT_USER.companyId}
	
		
			and t.catalog_id in
			
				#{item}
			 
		
		
			and t.catalog_id = #{catalogId, jdbcType=VARCHAR} 
		
		
			and t.catalog_id in
			
				#{item}
			
		
		
			and t.catalog_id = #{catalogId, jdbcType=VARCHAR} 
		
	
	
		and t.item_name like '%'|| #{itemName, jdbcType=VARCHAR} ||'%'
	
	
		and t.item_code = #{itemCode}
	
	
		and ((t.STATUS = '0' and t.UPLOAD_STATUS = '0' and t.company_id = #{CURRENT_USER.companyId})
		or (t.STATUS = '1' and t.UPLOAD_STATUS = '0' and t.company_id = #{CURRENT_USER.companyId})
		or (t.STATUS = '2' and t.UPLOAD_STATUS = '0' and t.company_id = #{CURRENT_USER.companyId})
		or (t.STATUS = '1' and t.UPLOAD_STATUS = '1' and t.company_id = #{CURRENT_USER.companyId}) 
		or (t.STATUS = '2' and t.UPLOAD_STATUS = '1' and t.company_id = #{CURRENT_USER.companyId}))
	
	
		and t.STATUS = '0' and t.UPLOAD_STATUS = '1'
	

) m
where rn > #{minRow, jdbcType=INTEGER} and rn <=#{maxRow, jdbcType=INTEGER}

select count(t.item_id)
	from biz_tiol_item t where 1 = 1 and t.company_id = #{CURRENT_USER.companyId}
	
		
			and t.catalog_id in
			
				#{item}
			 
		
		
			and t.catalog_id = #{catalogId, jdbcType=VARCHAR} 
		
		
			and t.catalog_id in
			
				#{item}
			
		
		
			and t.catalog_id = #{catalogId, jdbcType=VARCHAR} 
		
	
	
		and t.item_name like '%'|| #{itemName, jdbcType=VARCHAR} ||'%'
	
	
		and t.item_code = #{itemCode}
	
	
		and ((t.STATUS = '0' and t.UPLOAD_STATUS = '0' and t.company_id = #{CURRENT_USER.companyId})
		or (t.STATUS = '1' and t.UPLOAD_STATUS = '0' and t.company_id = #{CURRENT_USER.companyId})
		or (t.STATUS = '2' and t.UPLOAD_STATUS = '0' and t.company_id = #{CURRENT_USER.companyId})
		or (t.STATUS = '1' and t.UPLOAD_STATUS = '1' and t.company_id = #{CURRENT_USER.companyId}) 
		or (t.STATUS = '2' and t.UPLOAD_STATUS = '1' and t.company_id = #{CURRENT_USER.companyId}))
	
	
		and t.STATUS = '0' and t.UPLOAD_STATUS = '1'

select tm.item_id as "itemId",tmt.type_name as "meetingName" from 
tiol_item_meeting tm
left join biz_tiol_meeting_type tmt	on tm.type_id = tmt.type_id 
where tm.item_id in 
(
	select m.item_id from (
		select ROWNUM rn,t.item_id
		from biz_tiol_item t 
		where 1=1
		
			
				and t.catalog_id in
				
					#{item}
				 
			
			
				and t.catalog_id = #{catalogId, jdbcType=VARCHAR} 
			
			
				and t.catalog_id in
				
					#{item}
				
			
			
				and t.catalog_id = #{catalogId, jdbcType=VARCHAR} 
			
		
		
			and t.company_id in
			
				#{item}
			
		
	) m
	where rn > #{minRow, jdbcType=INTEGER} and rn <=#{maxRow, jdbcType=INTEGER}
) order by tm.order_number asc

select m.* from (
	select t.item_name as "itemName",t.item_code as "itemCode",ROWNUM rn,t.item_id as "itemId"
	from biz_tiol_item t
	where 1=1
	
		and substr(t.item_code, 0, 1) = #{levelCode, jdbcType=VARCHAR}
	
	
		and t.company_id = #{companyId, jdbcType=VARCHAR}
	
	
		and t.catalog_id = #{catalogId, jdbcType=VARCHAR}
	
	
		and t.item_name like '%'|| #{itemName, jdbcType=VARCHAR} ||'%'
	
	order by t.order_number asc
) m
where rn > #{minRow, jdbcType=INTEGER} and rn <=#{maxRow, jdbcType=INTEGER}

select count(*) from (
select t.item_name as "itemName",t.item_code as "itemCode",ROWNUM rn,t.item_id as "itemId"
from biz_tiol_item t
where 1=1

	and substr(t.item_code, 0, 1) = #{levelCode, jdbcType=VARCHAR}


	and t.company_id = #{companyId, jdbcType=VARCHAR}


	and t.company_id = #{CURRENT_USER.companyId, jdbcType=VARCHAR}


	and t.catalog_id = #{catalogId, jdbcType=VARCHAR}


	and t.item_name like '%'|| #{itemName, jdbcType=VARCHAR} ||'%'


	and t.item_code = #{itemCode, jdbcType=VARCHAR}

)

select tm.item_id as "itemId",CONCAT(CONCAT(tm.order_number, '、'),tmt.type_name) as "meetingName" from 
tiol_item_meeting tm
left join biz_tiol_meeting_type tmt	on tm.type_id = tmt.type_id 
where tm.item_id in (
	select m.item_id from (
		select ROWNUM rn,t.item_id
		from biz_tiol_item t 
		where 1=1
			
				and substr(t.item_code, 0, 1) = #{levelCode, jdbcType=VARCHAR}
			
			
				and t.company_id = #{companyId, jdbcType=VARCHAR}
			
			
				and t.catalog_id = #{catalogId, jdbcType=VARCHAR}
			
			
				and t.item_name like '%'|| #{itemName, jdbcType=VARCHAR} ||'%'
			
	) m
	where rn > #{minRow, jdbcType=INTEGER} and rn <=#{maxRow, jdbcType=INTEGER}
) order by tm.order_number asc

select m.* from (
	select t.item_name as "itemName",t.item_code as "itemCode",ROWNUM rn,t.item_id as "itemId"
	from biz_tiol_item t 
	where t.CATALOG_ID = #{catalogId} and t.company_id = 'GZW' and 1=1
) m
where rn > #{minRow, jdbcType=INTEGER} and rn <=#{maxRow, jdbcType=INTEGER}

select count(t.item_id) from biz_tiol_item t where 1=1
and t.CATALOG_ID = #{catalogId}
and t.company_id = 'GZW'

mapper.update:
update biz_tiol_item set itemCode = #{itemCode},item_name = #{itemName},
update_time = to_char(sysdate,'yyyy-mm-dd hh24:mi:ss'),update_use = #{CURRENT_USER.userId,jdbcType=VARCHAR}
where item_id = #{itemId} and company_id = 'GZW'

update biz_tiol_item set item_code = #{itemCode},item_name = #{itemName},
legal_flag = #{legalFlag},remark = #{remark},
update_time = to_char(sysdate,'yyyy-mm-dd hh24:mi:ss'),update_use = #{CURRENT_USER.userId,jdbcType=VARCHAR}
where item_id = #{itemId}

mapper.select:
select t.item_name as "itemName",t.item_code as "itemCode",ROWNUM rn,t.item_id as "itemId",t.legal_flag as "legalFlag",t.remark as "remark"
	from biz_tiol_item t 
	where item_id = #{itemId}

select count(*) from BIZ_TIOL_ITEM where 
 company_id = #{CURRENT_USER.companyId} and STATUS = 1
 
 	and ITEM_CODE = #{itemCode}
 
 
 	and ITEM_NAME = #{itemName}
