mapper.select:
select wm_concat(name) as name,wm_concat(position) as position,group_type from (select m.name, m.position, m.group_type,mt.order_number from biz_tiol_company_member m, tiol_company_member_type mt where m.status = 1 and mt.status = 1 and m.position != 'null' and m.position is not null and m.group_type = mt.type_name
and m.company_id = #{param.companyId, jdbcType=VARCHAR}
and m.GROUP_TYPE = #{param.groupType, jdbcType=VARCHAR}
order by m.order_number asc) group by group_type, order_number order by order_number asc

select * from biz_tiol_company_member where status = 1

	and company_id = #{companyId, jdbcType=VARCHAR}


	and GROUP_TYPE = #{groupType, jdbcType=VARCHAR}

order by order_number asc
